mapper.update:
UPDATE member SET m_pw = #{m_pw}, m_nick = #{m_nick}, m_name = #{m_name},
m_email = #{m_email}, m_birth = #{m_birth}, m_rank = #{m_rank}
WHERE m_id = #{m_id}

SELECT B.* FROM
(SELECT ROWNUM m_rn, A.* FROM
(SELECT * FROM MEMBER ORDER BY M_RANK, M_BIRTH)A)B
WHERE m_rn BETWEEN #{startNum}
AND #{lastNum};
